mapper.update:
update employee
set status=#{status}
where employeeid = #{employeeid}

mapper.select:
select *
from employee
where status = #{status} limit #{offset}
    , #{limit}

select count(*)
from employee

    where status = #{status}

select * from employee


        status = #{empCond.status}


        and employeename like concat('%',#{empCond.employeename},'%')


        and username like concat('%',#{empCond.username},'%')


limit #{offset},#{limit}
