mapper.delete:
UPDATE meeting
SET status = '3', canceledTime = #{canceledTime},canceledreason = #{canceledreason}
WHERE meetingid = #{id}

mapper.select:
SELECT COUNT(*)
FROM meeting
WHERE roomid = #{roomId}
  AND status != '3'  -- 正在进行的会议
AND (
    (starttime BETWEEN #{startTime}
  AND #{endTime})
   OR (endtime BETWEEN #{startTime}
  AND #{endTime})
   OR (starttime <= #{startTime}
  AND endtime >= #{endTime})
    )
